from random import *
import csv

#---------------------------------------------------------VARIABLES----------------------------------------------------------#

#_______________________BSN System____________________#


BATTERY_LEVEL=[]

#___________________Sensors Module____________________#

SPO2=[] # blood oxidation and blood oxidation curve (Oximeter)
